import React from 'react';

const Item = (props) => {
    return (
        <li key={props.id}>
        <div>   
        {Array.isArray(props.name) ? props.name.map((item, i) => <h3>{item}</h3>) : <h3>{props.name}</h3>}
        </div>

            <p>To be brought by: {props.user}</p>
            <button onClick={() => props.removeItem(props.id)}>Remove Item!</button>
        </li>
    );
}

export default Item;
