import React, { Component } from 'react';
import './App.css';
import firebase from './firebase.js';

class App extends Component {
	constructor() {
		super();
		this.state = {
			currentItem: '',
			userName: '',
			// Create a variable to hold items that are in the Firebase database, for printing to page.
			items: []
		};
		this.handleChange = this.handleChange.bind(this);
		this.handleSubmit = this.handleSubmit.bind(this);
	}

	handleChange(e) {
		this.setState({
			[e.target.name]: e.target.value
		});
	}

	handleSubmit(e) {
		// Prevent referesh when submit(Add Item) button is pressed
		e.preventDefault();
		// Create a space in Firebase database to store users' items, using .ref into 'items'
		const itemsRef = firebase.database().ref('items');
		// Grab input info (item and username) from state to send to Firebase database
		const item = {
			item: this.state.currentItem,
			user: this.state.userName
		};
		// Create a copy of object and push to Firebase database
		itemsRef.push(item);
		// Clear out inputs
		this.setState({
			currentItem: '',
			userName: ''
		});
	}

	componentDidMount() {
		const itemsRef = firebase.database().ref('items');
		itemsRef.on('value', (snap) => {
			let items = snap.val();
			let newState = [];
			for (let item in items) {
				newState.push({
					id: tem,
					item: items[item].item,
					user: items[item].user
				});
			}
		});
	}

	render() {
		return (
			<div className="app">
				<header>
					<div className="wrapper">
						<h1>What We Be Bringing!</h1>
					</div>
				</header>
				<div className="container">
					<section className="add-item">
						<form onSubmit={this.handleSubmit}>
							<input
								type="text"
								name="userName"
								placeholder="What do people call you?"
								onChange={this.handleChange}
								value={this.state.userName}
							/>
							<input
								type="text"
								name="currentItem"
								placeholder="What will you be bringing?"
								onChange={this.handleChange}
								value={this.state.currentItem}
							/>
							<button>Add Item</button>
						</form>
					</section>
					<section className="display-item">
						<div className="wrapper">
							<ul />
						</div>
					</section>
				</div>
			</div>
		);
	}
}

export default App;
