import Item from './Item.js';

class Results extends Component {
	transformData() {
		const {items} = this.props;
		const newData = items.reduce((acc, curr) => {
			const exists = acc.filter(item => item.user === curr.user);
			console.log(exists);
			if (exists) {
				console.log(exists);
				exists.item = [exists.item, curr.item];

				// return exists;
			} 
			return acc.concat({
				user: curr.user,
				item: curr.item
			})
		}, []);
		console.log(newData);
	}

	render() {
		this.transformData();
		return (
			<section className="display-item">
				<ul>
					{/* {this.props.items.map((item) => { console.log(this.props.items)
						return <Item id={item.id} removeItem={this.props.removeItem} name={item.item} user={item.user} />;
					})} */}
					{/* {props.items.map((item) => {
						return <Item id={item.id} removeItem={props.removeItem} name={item.item} user={item.user} />;
					})} */}
				</ul>
			</section>
		);
	}
};

export default Results;
